mapper.update:
UPDATE product
PROD_NAME = #{prodName} , PROD_DETAIL = #{prodDetail}, MANUFACTURE_DAY = #{manuDate} , PRICE = #{price} , IMAGE_FILE = #{fileName}
WHERE PROD_NO = #{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT PROD_NO , PROD_NAME , PRICE , REG_DATE FROM product
PROD_NO = #{searchKeyword}
PROD_NAME = #{searchKeyword}
PRICE = #{searchKeyword}
ORDER BY PROD_NO ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(SELECT PROD_NO , PROD_NAME , PRICE , REG_DATE 
										FROM product
										
											
												
									 				PROD_NO = #{searchKeyword}
												
												
